package JavaFullStack.BasicInJava.typesofvariable;

public class localvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       localvariable obj = new localvariable();
       obj.pradeep();
       obj.tiwin();
       obj.thilak();
	}
  public void pradeep()
  {
     String name = "PRADEEP";
     int age = 28;
     long mobileno = 7904201927l;
     String collegname="CSI COLLEGE";
     boolean mybool = true;
     
     System.out.println("MY NAME IS :" + name);
     System.out.println(age);
     System.out.println(mobileno);
     System.out.println(collegname);
     System.out.println("THIS ADRESS ARE : " + mybool);
  }
  public void tiwin()
  {
     String name = "TIWIN";
     int age = 26;
     long mobileno = 1234567890l;
     String collegname="AVS COLLEGE";
     boolean mybool = true;
     
     System.out.println("MY NAME IS :" + name);
     System.out.println(age);
     System.out.println(mobileno);
     System.out.println(collegname);
     System.out.println("THIS ADRESS ARE : " + mybool);
  }
  public void thilak()
  {
     String name = "THILAK";
     int age = 27;
     long mobileno = 7904212345l;
     String collegname="SONA COLLEGE";
     boolean mybool = true;
     
     System.out.println("MY NAME IS :" + name);
     System.out.println(age);
     System.out.println(mobileno);
     System.out.println(collegname);
     System.out.println("THIS ADRESS ARE : " + mybool);
  }
}


