package operatores;

public class arrithmetricoperatore {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//	 +,-,*,/,%----- its called arrithmatric operatores
//	 /-- divided values
//	 %---- reminder values
		
		int a = 100;
		int b = 15;
		int c = a+b;
		int d = a-b;
		int e = a/b;
		int f = a%b;
		
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);

	}

}
